mapper.select:
select subject,startDate,endDate,memo,num 
from schedule
where id= #{id}

select subject,(SELECT TO_CHAR(startDate,'YYYY-MM-DD hh:mi') FROM DUAL) as startDate,(SELECT TO_CHAR(endDate,'YYYY-MM-DD hh:mi') FROM DUAL) as endDate,memo,num
from schedule
where id=#{id}
and num=#{num}

mapper.update:
update schedule 
set subject=#{subject},
startDate=to_date(#{startDate},'YYYY-MM-DD hh24:mi'),
endDate=to_date(#{endDate},'YYYY-MM-DD hh24:mi'),
memo=#{memo} 
where id=#{id}
and num=#{num}

mapper.delete:
delete
from
schedule
where id=#{id}
and num=#{num}
